CREATE TABLE NotificationList
(
    EVENT_ID INT PRIMARY KEY NOT NULL,
    EVENT_NAME VARCHAR(127),
    _DATE INT DEFAULT 0,
);

DELETE FROM NotificationList;
INSERT INTO NotificationList
SELECT EVENT_ID, EVENT_NAME, _DATE
FROM EventList
WHERE EventList.NOTIFY=1 OR EventList.REGISTERED=0;

CREATE TABLE Interest
(
    _INTEREST VARCHAR(255) NOT NULL,
    SELECTED INT DEFAULT 0
);
